mapper.update:
MERGE INTO WEB_FIELD_DETAIL A /* 병합쿼리 새로운 데이터 A */ USING (
SELECT #{item.seq,jdbcType=VARCHAR} SEQ , #{item.fieldSeq} FIELD_SEQ , #{item.fieldSubName} FIELD_SUB_NAME , #{item.fieldRental} FIELD_RENTAL , #{item.fieldShower} FIELD_SHOWER , #{item.fieldParking} FIELD_PARKING , #{item.fieldDrink} FIELD_DRINK , #{item.fieldInfo} FIELD_INFO , #{item.orderNo} ORDER_NO , #{item.delYn} DEL_YN FROM DUAL
) B /* 기존데이터 B */ ON ( A.SEQ = B.SEQ ) /* 조건 */ WHEN MATCHED THEN /* 조건이 맞으면 아래쿼리 실행*/ UPDATE SET A.FIELD_SEQ = B.FIELD_SEQ , A.FIELD_SUB_NAME = B.FIELD_SUB_NAME , A.FIELD_RENTAL = B.FIELD_RENTAL , A.FIELD_SHOWER = B.FIELD_SHOWER , A.FIELD_PARKING = B.FIELD_PARKING , A.FIELD_DRINK = B.FIELD_DRINK , A.FIELD_INFO = B.FIELD_INFO , A.ORDER_NO = B.ORDER_NO , A.DEL_YN = B.DEL_YN DELETE WHERE B.DEL_YN = 'Y' WHEN NOT MATCHED THEN /* 조건이 다르면 INSERT */ INSERT(A.SEQ, A.FIELD_SEQ, A.FIELD_SUB_NAME, A.FIELD_RENTAL, A.FIELD_SHOWER, A.FIELD_PARKING, A.FIELD_DRINK, A.FIELD_INFO, A.ORDER_NO, A.DEL_YN) VALUES(WEB_FIELD_DETAIL_SEQ.NEXTVAL, #{seq}, B.FIELD_SUB_NAME, B.FIELD_RENTAL, B.FIELD_SHOWER, B.FIELD_PARKING, B.FIELD_DRINK, B.FIELD_INFO, B.ORDER_NO, B.DEL_YN)
